INSERT INTO `fotos_index` VALUES (17,'Dip. Hernán Cortés y Jaime Díaz','<p>Los diputados Hern&aacute;n Cort&eacute;s Berumen y Jaime D&iacute;az Bambrila, conscientes de la importancia de una Reforma Estructural en materia de Movilidad escuchan a MOVIDEMO, un colectivo de ciudadanos cuyo objetivo es el de trabajar en conjunto entre legisladores, gobiernos municipales y gobierno del estado.</p>\r\n<p>&nbsp;</p>','001.jpg','',2,1,0,0,0,'2013-04-08'),(21,'Dip. Jaime Díaz Brambila','<p>El diputado Jaime D&iacute;az Brambila, en reuni&oacute;n de trabajo con el colectivo ciudadano MOVIDEMO.</p>\r\n<p>&nbsp;</p>','005.jpg','',2,1,0,0,0,'2013-04-12'),(20,'Dip. Hernán Cortés y Victor Sánchez','<p>Los diputados Hern&aacute;n Cort&eacute;s Berumen y Victor S&aacute;nchez Orozco, en reuni&oacute;n de trabajo con el colectivo ciudadano MOVIDEMO.</p>\r\n<p>&nbsp;</p>','004.jpg','',2,1,0,0,0,'2013-04-12'),(18,'Dip. Guillermo Martínez Mora','<p>En Rueda de Prensa dip Guillermo Mart&iacute;nez Mora<strong>,</strong> anunci&oacute; Foro para recibir propuestas sobre la Ley Reglamentaria de Educaci&oacute;n.</p>\r\n<p>&nbsp;</p>','002.jpg','',3,1,0,0,0,'2013-04-10'),(19,'Dip. Gildardo Guerrero Torres','<p>En sesi&oacute;n de la Junta de Coordinaci&oacute;n Pol&iacute;tica, Diputado Gildardo Guerrero Torres.</p>\r\n<p>&nbsp;</p>','003.jpg','',2,1,0,0,0,'2013-04-11'),(22,'Dip. Mariana Arámbula y Alberto Esquer','<p>Diputada Mariana Ar&aacute;mbula Mel&eacute;ndez y<strong> </strong>Alberto Esquer Guti&eacute;rrez en el Foro de Consulta P&uacute;blica, Normativa en materia de Desarrollo Humano y Familia. Auditorio \"Adolfo Aguilar Zinder\". Cd. Guzm&aacute;n.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>','006.jpg','',1,1,0,0,0,'2013-04-12'),(23,'Dip. Gildardo Guerrero Torres','<p>Con el compromiso de lograr mejoras en la movilidad de la ciudad el Dip. Gildardo Guerrero Torres acude en bicicleta a la reuni&oacute;n de Asuntos metropolitanos, misma que se realizar&aacute; en el parque Metropolitano.</p>\r\n<p>&nbsp;</p>','007.jpg','',2,1,0,0,0,'2013-04-15'),(24,'Dip. Alberto Esquer Gutiérrez','<p>El Diputado Alberto Esquer Guti&eacute;rrez del GPPAN present&oacute; iniciativa para un Nuevo Instituto Electoral y Participaci&oacute;n Ciudadana, donde se contempla una reducci&oacute;n de siente a cinco Consejeros y la Presidencia del Instituto sea rotativa y elegida por los mismos Consejeros.&nbsp;</p>\r\n<p>As&iacute; mismo una Iniciativa donde contempla se realice Examen de Control de Confianza a todos los Secretarios de Estado del Gobierno de Jalisco.</p>\r\n<p>&nbsp;</p>','008.jpg','',1,1,0,0,0,'2013-04-18'),(25,'Dip. Juan Carlos Márquez Rosas','<p>Diputado del GPPAN, Juan Carlos M&aacute;rquez Rosas, present&oacute; Iniciativa para regular el uso de la fuerza p&uacute;blica de las corporaciones de seguridad, donde es necesaria la capacitaci&oacute;n de los elementos de c&oacute;mo actuar en momentos de crisis y as&iacute; poder ejercer su funci&oacute;n sin temor a sanciones injustas.</p>\r\n<p>&nbsp;</p>','009.jpg','',1,1,0,0,0,'2013-04-18'),(26,'Dip. Juan José Cuevas García','<p>Juan Jos&eacute; Cuevas Garc&iacute;a, Diputado del GPPAN, exhort&oacute; al Gobierno del Estado a la Secretar&iacute;a de Educaci&oacute;n Federal y la Universidad de Guadalajara, expida credencial a sus estudiantes en un plazo de 10 d&iacute;as del inicio del ciclo escolar, en pro de los credencializados puedan acceder a sus beneficios tales como los trasvales.</p>\r\n<p>&nbsp;</p>','010.jpg','',1,1,0,0,0,'2013-04-18'),(27,'Dip. Victor Sánchez Orozco','<p>Diputado Victor Manuel S&aacute;nchez Orozco, del GPPAN, present&oacute; un exhorto al Gobierno del Estado para sancionar al Coordinador de Asesores del Ejecutivo, H&eacute;ctor Vielma Ord&oacute;&ntilde;ez, por encontrarse en el maraton de Boston en d&iacute;a h&aacute;bil.</p>\r\n<p>&nbsp;</p>','011.jpg','',1,1,0,0,0,'2013-04-18'),(28,'Dip. Victor Sánchez Orozco','<p>Victor Manuel S&aacute;nchez Orozco, diputado del GPPAN, realiz&oacute; un exhorto al Gobernador del Estado a buscar los mecanismos adecuados para que el Transporte P&uacute;blico sea Gratuito para todos los Estudiantes en Jalisco.</p>\r\n<p>&nbsp;</p>','012.jpg','',1,1,0,0,0,'2013-04-18'),(29,'Dip. Gildardo Guerrero Torres','<p>Diputado Gildardo Guerrero Torres, exhort&oacute; al Presidente Municipal de Guadalajara, Ramiro Hern&aacute;ndez Garc&iacute;a a trabajar para mejorar la Seguridad en el municipio.</p>\r\n<p>&nbsp;</p>','013.jpg','',1,1,0,0,0,'2013-04-18'),(30,'Dip. Gildardo Guerrero Torres','<p>El Diputado Gildardo Guerrero Torres, del GPPAN, present&oacute; Iniciativa en donde donde propone eliminar el Fuero a todos los Funcionarios P&uacute;blicos del Estado de Jalisco.</p>\r\n<p>&nbsp;</p>','014.jpg','',1,1,0,0,0,'2013-04-18'),(31,'Dip. Mariana Arámbula Meléndez','<p>La Diputada Mariana Ar&aacute;mbula Mel&eacute;ndez, del GPPAN, present&oacute; iniciativa en pro de personas con discapacidad, para generar programa de difusi&oacute;n de beneficios que corresponde por Ley.</p>\r\n<p>&nbsp;</p>','015.jpg','',1,1,0,0,0,'2013-04-18'),(32,'Dip. Mariana Arámbula Meléndez','<p>Dip. del GPPANJAL Mariana Ar&aacute;mbula Mel&eacute;ndez, present&oacute; una iniciativa para proteger a los menores en las escuelas para detectar casos sujetos de abusos.</p>\r\n<p>&nbsp;</p>','016.jpg','',1,1,0,0,0,'2013-04-18'),(33,'Dip. Jaime Díaz Brambila','<p class=\"MsoNoSpacing\" style=\"text-align: justify;\"><span style=\"font-size: 10pt; font-family: \'Century Gothic\', sans-serif; color: #333333; background-position: initial initial; background-repeat: initial initial;\">Diputado Jaime Ismael D&iacute;az Brambila, present&oacute; iniciativa que Reforma y Adiciona diversos art&iacute;culos de la Ley Org&aacute;nica del Poder Judicial del Estado de Jalisco.; Decreto que reforma diversos art&iacute;culos de la Ley de Desarollo Forestal Sustentable para el Estado de Jalisco; realiz&oacute; un exhorto al Secretario General del Congreso del Estado de Jalisco y a la Comisi&oacute;n de Administraci&oacute;n, respecto a los nombramientos otorgados por la LIX Legislatura.</span></p>','017.jpg','',1,1,0,0,0,'2013-04-18'),(34,'Dip. Guillermo Martínez Mora','<p>En el inicio del Foro \"Reforma Educativa: Co-responsabilidad del Estado, Maestros, Padres de Familia y Alumnos\" del diputado del Grupo Parlamentario de Acci&oacute;n Nacional, Guillermo Mart&iacute;nez Mora</p>','018.jpg','',4,1,0,0,0,'2013-04-19'),(35,'Dip. Mariana Arámbula, Norma Cordero, Gildardo Guerrero y Juan José Cuevas','<p>Diputados de Acci&oacute;n Nacional, Mariana Ar&aacute;mbula Mel&eacute;ndez, Norma Ang&eacute;lica Cordero Prado, Gildardo Guerrero Torres, y Juan Jos&eacute; Cuevas Garc&iacute;a, presentes en el Foro, Reforma Educativa.</p>\r\n<p>&nbsp;</p>','019.jpg','',4,1,0,0,0,'2013-04-19'),(37,'Dip. Gildardo Guerrero y Victor Sánchez','<p>Los Diputados de Acci&oacute;n Nacional, Gildardo Guerrero Torres y Victor S&aacute;nchez Orozco, acompa&ntilde;an al Grupo Edilicio del PAN en el Ayuntamiento de Guadalajara, coordinado por el Regidor, Alberto C&aacute;rdenas Jim&eacute;nez a denunciar el mal manejo de recursos del Municipio.</p>','021.jpg','',3,1,0,0,0,'2013-04-22'),(36,'Dip. José Luis Munguía Cardona','<p>El Diputado del PAN, Jos&eacute; Luis Mung&iacute;a Cardona, Presidente de la Comisi&oacute;n de Seguridad P&uacute;blica en el Congreso del Estado, en la segunda reuni&oacute;n extraordinaria \"Red de Prevenci&oacute;n del Delito\" con diversas dependencias de Gobierno e Instituciones.</p>\r\n<p>&nbsp;</p>','020.jpg','',2,1,0,0,0,'2013-04-22'),(41,'Dip. Juan José Cuevas García','<p>En la Comisi&oacute;n de Salud que preside el diputado del GPPANJAL El&iacute;as Octavio &Iacute;&ntilde;iguez Mej&iacute;a y vocal Juan Jos&eacute; Cuevas Garc&iacute;a, con la presencia de diversas instancias de salud en el estado.</p>\r\n<p>&nbsp;</p>','025.jpg','',2,1,0,0,0,'2013-04-25'),(39,'Dip. Gildardo Guerrero y Victor Sánchez','<p>En Rueda de Prensa el Presidente de Comit&eacute; Directivo Estatal del PAN, Miguel Monr&aacute;z Ibarra, y los diputados panistas, Gildardo Guerrero Torres y Victor S&aacute;nchez Orozco, exigen al Gobernador del Estado cumpla #Transpublicogratis a todos los estudiantes de Jalisco.</p>','023.jpg','',1,1,0,0,0,'2013-04-23'),(38,'Dip. Gildardo Guerrero Torres','<p>El diputado del PAN, Gildardo Guerero Torres, se&ntilde;ala que los delitos en Guadalajara, son por falta de recursos a polic&iacute;as. #GDLInsegura</p>','022.jpg','',3,1,0,0,0,'2013-04-22'),(40,'Dip. Elías Íñiguez y Norma Cordero','<p>En la Comisi&oacute;n de Salud que preside el diputado del PAN, El&iacute;as Octavio &Iacute;&ntilde;iguez Mej&iacute;a y vocal Norma Ang&eacute;lica Cordero Prado, con la presencia de diversas instancias de salud en el estado.</p>\r\n<p>&nbsp;</p>','024.jpg','',2,1,0,0,0,'2013-04-24'),(42,'Dip. Mariana Arámbula Meléndez','<p>En la Comisi&oacute;n de Derechos Humanos del Congreso del Estado, participando como vocal, la diputada panista, Mariana Ar&aacute;mbula Mel&eacute;ndez, con la presencia de Fiscal de Derechos Humanos, Mtra Lizzeth Hern&aacute;ndez.</p>\r\n<p>&nbsp;</p>','026.jpg','',2,1,0,0,0,'2013-04-24'),(43,'Dip. Gabriela Andalón Becerra','<p>En la Comisi&oacute;n de Derechos Humanos del Congreso del Estado, participando como vocal, la diputada del GPPANJAL, Gabriela Andal&oacute;n Becerra, con la presencia de Fiscal de Derechos Humanos, Mtra Lizzeth Hern&aacute;ndez.</p>\r\n<p>&nbsp;</p>','027.jpg','',2,1,0,0,0,'2013-04-24'),(44,'Dip. Gabriela Andalón Becerra','<p>La diputada de Acci&oacute;n Nacional, Gabriela Andal&oacute;n Becerra, participando en la Comisi&oacute;n Especial para la Reforma y Reingenier&iacute;a del Poder Legislativo</p>\r\n<p>&nbsp;</p>','028.jpg','',2,1,0,0,0,'2013-04-24'),(45,'Dip. Jaime Díaz Brambila','<p>Participando como vocal el diputado del PAN Jaime Ismael D&iacute;az Brambila, de la Comisi&oacute;n de Medio Ambiente y Desarrollo Sustentable.</p>','029.jpg','',2,1,0,0,0,'2013-04-24'),(46,'Dip. Guillermo Martínez Mora','<p>En la Comisi&oacute;n de Educaci&oacute;n presidida por el diputado del GPPANJAL, Guillermo Mart&iacute;nez Mora, se dio a conocer el An&aacute;lisis Preliminar del Foro \"Reforma Educativa: Cooresponsabilidad del Estado, Maestros, Padres de Familia y Alumnos\"</p>\r\n<p>&nbsp;</p>','030.jpg','',2,1,0,0,0,'2013-04-24'),(47,'Dip. Mariana Arámbula, Norma Cordero, Juan José Cuevas','<p>Asistieron a la Comisi&oacute;n de Educaci&oacute;n los diputados del PAN, Norma Ang&eacute;lica Cordero Prado, Mariana Ar&aacute;mbula Mel&eacute;ndez y Juan Jos&eacute; Cuevas Garc&iacute;a.</p>\r\n<p>&nbsp;</p>','031.jpg','',1,1,0,0,0,'2013-04-24'),(48,'Dip. José Luis Munguía Cardona','<p>El Legislador de Acci&oacute;n Nacional, Jos&eacute; Luis Mungu&iacute;a Cardona, en Rueda de Prensa, con el tema \"Mando &Uacute;nico Policial en Jalisco\"</p>\r\n<p>&nbsp;</p>','IMG_4742.JPG','',3,1,0,0,0,'2013-06-25'),(49,'Dip. Guillermo Martínez Mora','<p>Rueda de Prensa del diputado del PAN, Guillermo Mart&iacute;nez Mora, Conclusiones \"Foro de Reforma Educativa: Coorresponsabilidad del Estado, Maestros, Padres de Familia y Alumnos\" que permitir&aacute; enviar propuesta en torno a leyes secundarias de educaci&oacute;n al Congreso de la Uni&oacute;n.</p>\r\n<p>&nbsp;</p>','img_4810.jpg','',3,1,0,0,0,'2013-06-26'),(50,'Dip. Elías Octavio Íñiguez Mejía','<p>En el Foro \"Acoso Escolar; un problema de Salud Mental\" organizado por el diputado de Acci&oacute;n Nacional presidente de la Comisi&oacute;n de Higiene y Salud, El&iacute;as Octavio &Iacute;&ntilde;iguez.&nbsp;</p>\r\n<p>&nbsp;</p>','IMG_4860.JPG','',4,1,0,0,0,'2013-06-27'),(51,'Dip. Gildardo Guerrero, Victor Sánchez y Juan José Cuevas','<p>Rueda de Prensa del Grupo Parlamentario de Acci&oacute;n Nacional,&nbsp;da a conocer m&aacute;s irregularidades de la LIX Legislatura de auditorias innecesarias al Congreso del Estado</p>\r\n<p>&nbsp;</p>','RP_030713.JPG','',3,1,0,0,0,'2013-07-03'),(52,'Dip. Gildardo Guerrero, Hernán Cortés, José Luis Munguía y Juan Carlos Márquez','<p>En Rueda de Prensa el Grupo Parlamentario de Acci&oacute;n Nacional, presenta los avances logrados en la confecci&oacute;n de una nueva Ley de Transparencia para Jalisco.</p>\r\n<p>&nbsp;</p>','IMG_5640 - copia.JPG','',3,1,0,0,0,'2013-07-08'),(53,'Dip. Gildardo Guerrero y Elías Íñiguez','<p>El&nbsp;Grupo Parlamentario de Acci&oacute;n Nacional, en voz de los diputados Gildardo Guerrero y El&iacute;as &Iacute;&ntilde;iguez, recibieron a pescadores de la Presa de Valencia as&iacute; como regidores del municipio de Acatl&aacute;n de Ju&aacute;rez, los cuales dieron cuenta de los graves problemas que tuvo la presa por la contaminaci&oacute;n de melaza</p>\r\n<p>&nbsp;</p>','IMG_5915.JPG','',1,1,0,0,0,'2013-07-10'),(54,'Dip. Mariana Arámbula Meléndez','<p>Diputada Mariana Ar&aacute;mbula, reconoce a cinco j&oacute;venes que ganaron Torneo Internacional Robocup2013 en junio pasado, en Holanda; Los estudiantes de tercero de secundaria, de las escuelas publicas 116, 59 mixta y el Cecyte son: Nadia Carolina Moreno Bravo, Daniel Mart&iacute;nez Sandoval, Gustavo Lara Castillo, No&eacute; Alejandro Salinas y Seth Trevi&ntilde;o Valle.</p>','robocup2013.jpg','',2,1,0,0,0,'2013-07-30'),(55,'Dip. José Luis Munguía Cardona','<p>Diputado Jos&eacute; Luis Mungu&iacute;a en representaci&oacute;n del Poder Legislativo,&nbsp;<a href=\"https://www.facebook.com/hashtag/legislativojal\">&#8236;</a>en el&nbsp;evento&nbsp;<a href=\"https://www.facebook.com/hashtag/gobjal\">&#8234;del Gobierno del Estado,&#8236;</a>&nbsp;Fuerza &Uacute;nica Jalisco, Etapa &nbsp;de Capacitaci&oacute;n.</p>','tebe.jpg','',4,1,0,0,0,'2013-08-05');
/*!40000 ALTER TABLE `fotos_index` ENABLE KEYS */;
